import numpy as np
import math

BASE_DIR = os.path.abspath(".")
with open(os.path.join(BASE_DIR, "clean_dataset.json"), "r", encoding="utf-8") as file:
    products = json.load(file)


def get_top_shades(shade_rgb, relevant_products):
    """
    Given a shade and list of relevant products, return the closest shades for each relevant products
    ----------
    shade_rgb : list
        A list containing the 3-tuple RGB value
    products : dict
        A dict of relevant product as keys and their shade information as values
    Returns
    -------
    dict
        dict with unique index for each unique ingredient found in the dataset.
    """
    best_shades = {}
    for product in relevant_products:
        shade_info = relevant_products[product].replace(
            'array(', '').replace(', dtype=uint8)', '').replace(', 255', '').replace('ÃƒÂ©', 'é').replace('ÃƒÂ¨', 'è').replace(
                '[', '['
        )  # .encode('latin1')
        # print(shade_info)
        try:
            shade_info = eval(shade_info)
            shade_diff = np.zeros(len(shade_info))
            # print(product)
            for shade_i in range(len(shade_info)):
                bgr = (shade_info[shade_i][2][0])
                if isinstance(bgr, int):
                    bgr = [bgr, bgr, bgr]
                shade_diff[shade_i] = math.sqrt((bgr[0] - shade_rgb[0])**2 + (
                    bgr[1] - shade_rgb[1])**2 + (bgr[2] - shade_rgb[2])**2) / math.sqrt(195075)
            closest_i = np.argmin(shade_diff)
            # print(shade_info[closest_i])
            best_shades[product] = [shade_info[closest_i][0],
                                    shade_info[closest_i][1], shade_info[closest_i][2][0]]
        except SyntaxError:
            continue
            # for i in shade_info:  # .split(','):
            #     try:
            #         s = eval(i)
            #     except:
            #         print(i)
    # print(best_shades)
    return best_shades


